import React, { useState, useEffect } from "react";
import Calendar from "react-calendar";
import axios from "axios";
import "./Calandar.css";

const MaintenanceCalendar = () => {
  const [value, setValue] = useState(new Date());
  const [maintenanceData, setMaintenanceData] = useState([]);

  useEffect(() => {
    const fetchMaintenanceData = async () => {
      try {
        const response = await axios.get(
          "http://localhost:5000/maintenance/upcoming/",
          {
            withCredentials: true,
          }
        );
        setMaintenanceData(response.data);
      } catch (error) {
        console.error("Error fetching maintenance data:", error);
      }
    };

    fetchMaintenanceData();
  }, []);

  const formatDate = (date) => {
    return new Date(date).toISOString().split("T")[0];
  };

  const maintenanceByDate = maintenanceData.reduce((acc, item) => {
    const date = formatDate(item.maintenanceDate);
    if (!acc[date]) {
      acc[date] = [];
    }
    acc[date].push(item);
    return acc;
  }, {});

  const renderMaintenanceTasks = (date) => {
    const formattedDate = formatDate(date);
    return maintenanceByDate[formattedDate]?.map((task, index) => (
      <div
        key={index}
        style={{ margin: "10px", padding: "5px", border: "1px solid gray" }}
      >
        <p>Maintenance ID: {task.maintenanceID}</p>
        <p>Description: {task.maintenanceDescription}</p>
        <p>Cost: ${task.cost}</p>
        <p>Parts Replaced: {task.partsReplaced}</p>
      </div>
    ));
  };

  return (
    <div className="calendar-container">
      <Calendar
        onChange={setValue}
        value={value}
        tileContent={({ date, view }) =>
          view === "month" && renderMaintenanceTasks(date)
        }
      />
    </div>
  );
};

export default MaintenanceCalendar;
