// MainContent.jsx

import React, { useState, useEffect, Suspense } from "react";

// const ResponsiveTable = React.lazy(() => import("./ResponsiveTable")); // Assume this is your table component

const MainContent = ({ activeMenu }) => {
  const [data, setData] = useState(null);

  useEffect(() => {
    // This is where you'd make your database request.
    // For demonstration purposes, we're using a timeout to simulate a network request.
    const fetchData = async () => {
      try {
        // Simulate a network request
        const response = await new Promise((resolve) => {
          setTimeout(() => resolve("data fetched from the database"), 1000);
        });
        setData(response);
      } catch (error) {
        console.error("Failed to fetch data: ", error);
      }
    };

    if (activeMenu) {
      fetchData();
    }
  }, [activeMenu]);

  return (
    <div className="main-content">
      <Suspense fallback={<div>Loading...</div>}>
        {activeMenu && data ? (
          {
            /* <ResponsiveTable data={data} /> */
          }
        ) : (
          <div>Please select a menu item</div>
        )}
      </Suspense>
    </div>
  );
};

export default MainContent;
