import React, { useState, useEffect, Suspense } from "react";

const ResponsiveTable = React.lazy(() => import("./ResponsiveTable")); // Now uncommented

const MainContent = ({ activeMenu }) => {
  const [data, setData] = useState(null);

  useEffect(() => {
    // This function simulates fetching data based on the active menu item.
    const fetchData = async () => {
      try {
        // Simulate a network request
        const response = await new Promise((resolve) => {
          setTimeout(
            () => resolve("data fetched based on " + activeMenu),
            1000
          );
        });
        setData(response);
      } catch (error) {
        console.error("Failed to fetch data: ", error);
      }
    };

    if (activeMenu) {
      fetchData();
    }
  }, [activeMenu]);

  // Render the appropriate component based on activeMenu and data
  const renderContent = () => {
    if (activeMenu && data) {
      // Assuming the ResponsiveTable component takes a prop named `data`
      return <ResponsiveTable data={data} />;
    } else {
      return <div>Please select a menu item</div>;
    }
  };

  return (
    <div className="main-content">
      <Suspense fallback={<div>Loading...</div>}>{renderContent()}</Suspense>
    </div>
  );
};

export default MainContent;
