import React, { useState, useEffect } from "react";
import axios from "axios";

function Equipment() {
  const [equipmentData, setEquipmentData] = useState([]);
  const [selectedEquipment, setSelectedEquipment] = useState([]);

  useEffect(() => {
    async function fetchEquipmentData() {
      try {
        const response = await axios.get("http://localhost:5000/equipment/", {
          withCredentials: true,
        });
        const dataWithUniqueId = response.data.map((item, index) => ({
          ...item,
          uniqueKey: `equipment-${index}-${item.equipmentID}`,
        }));
        setEquipmentData(dataWithUniqueId);
      } catch (error) {
        console.error("Fetch error:", error);
      }
    }

    fetchEquipmentData();
  }, []);

  const handleCheckboxChange = (equipmentID) => {
    setSelectedEquipment((prevSelected) => {
      if (prevSelected.includes(equipmentID)) {
        return prevSelected.filter((id) => id !== equipmentID);
      } else {
        return [...prevSelected, equipmentID];
      }
    });
  };

  const handleFieldChange = (equipmentID, field, value) => {
    setEquipmentData(
      equipmentData.map((equipment) => {
        if (equipment.equipmentID === equipmentID) {
          equipment[field] = value;
        }
        return equipment;
      })
    );
  };

  const handleUpdate = async () => {
    try {
      await Promise.all(
        selectedEquipment.map((equipmentId) =>
          axios.put(`http://localhost:5000/equipment/update/${equipmentId}`, {
            withCredentials: true,
          })
        )
      );
    } catch (error) {
      console.error("Update error:", error);
    }
  };

  const handleRemove = async () => {
    try {
      await Promise.all(
        selectedEquipment.map((equipmentId) =>
          axios.delete(
            `http://localhost:5000/equipment/remove/${equipmentId}`,
            {
              withCredentials: true,
            }
          )
        )
      );
    } catch (error) {
      console.error("Remove error:", error);
    }
  };

  return (
    <div>
      <h2>Equipment List</h2>
      <table>
        <thead>
          <tr>
            <th>Select</th>
            <th>Equipment ID</th>
            <th>Name</th>
            <th>Type</th>
            <th>Entry Date</th>
          </tr>
        </thead>
        <tbody>
          {equipmentData.map((equipment) => (
            <tr key={equipment.uniqueKey}>
              <td>
                <input
                  type="checkbox"
                  checked={selectedEquipment.includes(equipment.equipmentID)}
                  onChange={() => handleCheckboxChange(equipment.equipmentID)}
                />
              </td>
              <td>
                <input
                  type="text"
                  value={equipment.equipmentID}
                  onChange={(e) =>
                    handleFieldChange(
                      equipment.equipmentID,
                      "equipmentID",
                      e.target.value
                    )
                  }
                />
              </td>
              <td>
                <input
                  type="text"
                  value={equipment.equipmentID}
                  onChange={(e) =>
                    handleFieldChange(
                      equipment.equipmentID,
                      "equipmentID",
                      e.target.value
                    )
                  }
                />
              </td>
              <td>
                <input
                  type="text"
                  value={equipment.equipmentDescription}
                  onChange={(e) =>
                    handleFieldChange(
                      equipment.equipmentID,
                      "equipmentDescription",
                      e.target.value
                    )
                  }
                />
              </td>
              <td>
                <input
                  type="text"
                  value={equipment.equipmentCondition}
                  onChange={(e) =>
                    handleFieldChange(
                      equipment.equipmentID,
                      "equipmentCondition",
                      e.target.value
                    )
                  }
                />
              </td>
              <td>
                <input
                  type="text"
                  value={equipment.createdAt}
                  onChange={(e) =>
                    handleFieldChange(
                      equipment.equipmentID,
                      "createdAt",
                      e.target.value
                    )
                  }
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <button onClick={handleUpdate} className="button">
        Update Selected
      </button>
      <button onClick={handleRemove} className="button">
        Remove Selected
      </button>
    </div>
  );
}

export default Equipment;
