import React, { useState, useEffect } from "react";
import axios from "axios";

function Maintenance() {
  const [maintenanceRecords, setMaintenanceRecords] = useState([]);
  const [selectedRecords, setSelectedRecords] = useState([]);

  useEffect(() => {
    async function fetchMaintenance() {
      try {
        const response = await axios.get("http://localhost:5000/maintenance/", {
          withCredentials: true,
        });
        setMaintenanceRecords(response.data);
      } catch (error) {
        console.error("Fetch error:", error);
      }
    }

    fetchMaintenance();
  }, []);

  const handleCheckboxChange = (selectedID) => {
    setSelectedRecords((prevSelected) => {
      if (prevSelected.includes(selectedID)) {
        return prevSelected.filter((id) => id !== selectedID);
      } else {
        return [...prevSelected, selectedID];
      }
    });
  };

  const handleRemove = async () => {
    if (selectedRecords.length === 0) {
      alert("Please select at least one record to remove.");
      return;
    }
    try {
      for (const recordID of selectedRecords) {
        await axios.delete(
          `http://localhost:5000/maintenance/delete/${recordID}`,
          { withCredentials: true }
        );
      }
      alert("Selected records removed successfully.");
      setSelectedRecords([]);
    } catch (error) {
      console.error("Error removing records:", error);
      alert("Error removing records.");
    }
  };

  const renderBoolean = (value) => {
    if (typeof value === "boolean") {
      return value ? "Yes" : "No";
    }
    return "N/A";
  };

  return (
    <div>
      <h1>Latest Maintenance Reports</h1>
      <table>
        <thead>
          <tr>
            <th>Select</th>
            <th>MaintenanceDescription</th>
            <th>MaintenanceDate</th>
            <th>VehicleTag</th>
            <th>Employee That File Entry</th>
            <th>Year Make Model</th>
            <th>EquipmentID</th>
            <th>Cost</th>
            <th>PartsReplaced</th>
            <th>Entry Date</th>
          </tr>
        </thead>
        <tbody>
          {formData.map((form) => (
            <tr key={form.id}>
              <td>
                <input
                  type="checkbox"
                  checked={selectedReport.includes(form.id)}
                  onChange={() => handleCheckboxChange(form.id)}
                />
              </td>
              <td>{form.maintenanceDescription}</td>
              <td>{form.tmaintenanceDateag}</td>
              <td>{form.vehicleInformation}</td>
              <td>{form.vehicleTag}</td>
              <td>{form.equipmentID}</td>
              <td>{form.cost}</td>
              <td>{form.partsReplaced}</td>
              <td>{form.date}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <button onClick={handleRemove} className="button">
        Remove Selected
      </button>
    </div>
  );
}

export default MaintenanceReportTable;
