import React, { useState, useEffect } from "react";
import axios from "axios";

function GetAllVehicles() {
  const [vehicles, setVehicles] = useState([]);
  const [selectedVehicles, setSelectedVehicles] = useState([]);

  useEffect(() => {
    async function fetchVehicles() {
      try {
        const response = await axios.get("http://localhost:5000/vehicles/", {
          withCredentials: true,
        });
        setVehicles(response.data);
      } catch (error) {
        console.error("Fetch error:", error);
      }
    }

    fetchVehicles();
  }, []);

  const handleCheckboxChange = (vehicleID) => {
    setSelectedVehicles((prevSelected) => {
      if (prevSelected.includes(vehicleID)) {
        return prevSelected.filter((id) => id !== vehicleID);
      } else {
        return [...prevSelected, vehicleID];
      }
    });
  };

  return (
    <div>
      <h2>All Vehicles</h2>
      <table>
        <thead>
          <tr>
            <th>Select</th>
            <th>ID</th>
            <th>Vehicle Name</th>
            <th>Vehicle Tag</th>
            <th>Vehicle Condition</th>
            <th>Last User</th>
          </tr>
        </thead>
        <tbody>
          {vehicles.map((vehicle) => (
            <tr key={vehicle.vehicleID}>
              <td>
                input type="checkbox" checked=
                {selectedVehicles.includes(vehicle.vehicleID)}
                onChange={() => handleCheckboxChange(vehicle.vehicleID)}
              </td>
              <td>{vehicle.vehicleID}</td>
              <td>{vehicle.vehicleName}</td>
              <td>{vehicle.vehicleTag}</td>
              <td>{vehicle.vehicleCondition}</td>
              <td>{vehicle.lastUser}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default GetAllVehicles;
