// rootReducer.js
import { combineReducers } from "redux";
import authReducer from "./authReducer"; // Import your auth reducer
import otherReducer from "./otherReducer"; // Import other reducers as needed

const rootReducer = combineReducers({
  auth: authReducer, // Example for an authentication slice of state
  other: otherReducer, // Example for another slice of state
  // Add more slices and corresponding reducers as needed
});

export default rootReducer;
