import React, { useState, useEffect } from "react";
import axios from "axios";

function MaintenanceReportTable() {
  const [formData, setFormData] = useState({
    vehicleTag: "",
    comment: "",
    airHydraulicBrakeCheck: null,
    parkingTrailerBrakeCheck: null,
    serviceBrakeCheck: null,
    lightingIndicators: null,
    emergencyEquipment: null,
    windshieldTrafficMonitoringDevices: null,
    wipersWashers: null,
    heaterDefroster: null,
    horns: null,
    allExternalLights: null,
    lensesFront: null,
    fluidLevels: null,
    fluidAirLeaks: null,
    steeringSystems: null,
    tires: null,
    rims: null,
    lugNuts: null,
    springsAirBagsShocks: null,
    brakeLinesHosesLeaks: null,
    brakeContaminates: null,
    lensesReflectorsSide: null,
    trafficMonitoringDevicesSide: null,
    battery: null,
    fuelTanks: null,
    frames: null,
    lensesReflectorsRear: null,
  });
  let [selectedReport, setSelectedReport] = useState([]);

  useEffect(() => {
    async function fetchReports() {
      try {
        const response = await axios.get("http://localhost:5000/maintenance/", {
          withCredentials: true,
        });
        setFormData(response.data);
      } catch (error) {
        console.error("Fetch error:", error);
      }
    }
    fetchReports();
  }, []);

  const handleRemove = async () => {
    if (selectedReport.length === 0) {
      alert("Please select at least one Report to remove.");
      return;
    }
    try {
      for (const reportID of selectedReport) {
        await axios.delete(
          `http://localhost:5000/local-inspections/delete/${reportID}`,
          { withCredentials: true }
        );
      }
      alert("Selected vehicles removed successfully.");

      setSelectedReport((prevReports) =>
        prevReports.filter((reportID) => !selectedReport.includes(reportID))
      );
    } catch (error) {
      console.error("Error removing Reports:", error);
      alert("Error removing Reports.");
    }
    // Clear the selection
    setSelectedReport([]);
  };

  const handleCheckboxChange = (reportID) => {
    setSelectedReport((prevSelected) => {
      if (prevSelected.includes(reportID)) {
        return prevSelected.filter((id) => id !== reportID);
      } else {
        return [...prevSelected, reportID];
      }
    });
  };

  const renderBoolean = (value) => {
    if (typeof value === "boolean") {
      return value ? "Yes" : "No";
    }
    return "N/A";
  };

  return (
    <div>
      <h1>Latest Maintenance Reports</h1>
      <table>
        <thead>
          <tr>
            <th>Select</th>
            <th>Report ID</th>
            <th>Comments</th>
            <th>Air Hydraulic Brake Check</th>
            <th>Parking Trailer Brake Check</th>
            <th>Service Brake Check</th>
            <th>Lighting Indicators</th>
            <th>Emergency Equipment</th>
            <th>Windshield Traffic Monitoring Devices</th>
            <th>Wipers Washers</th>
            <th>Heater Defroster</th>
            <th>Horn</th>
            <th>All External Lights</th>
            <th>Lenses Front</th>
            <th>Fluid Levels</th>
            <th>Fluid Air Leaks</th>
            <th>Steering Systems</th>
            <th>Tires</th>
            <th>Rims</th>
            <th>Lug Nuts</th>
            <th>Springs Air Bags Shocks</th>
            <th>Brake Lines Hoses Leaks</th>
            <th>Brake Contaminates</th>
            <th>Lenses Reflectors Side</th>
            <th>Traffic Monitoring DeviceSide</th>
            <th>Battery</th>
            <th>Fuel Tanks</th>
            <th>Frames</th>
            <th>Lenses Reflectors Rear</th>
            <th>Entry Date</th>
          </tr>
        </thead>
        <tbody>
          {formData.map((form) => (
            <tr key={form.id}>
              <td>
                <input
                  type="checkbox"
                  checked={selectedReport.includes(form.id)}
                  onChange={() => handleCheckboxChange(form.id)}
                />
              </td>
              <td>{form.vehicleTag}</td>
              <td>{form.tag}</td>
              <td>{form.vehicleInformation}</td>
              <td>{form.userName}</td>
              <td>{form.date}</td>
              <td>{form.mileage}</td>
              <td>{form.workTicket}</td>
              <td>{form.personReleasingVehicle}</td>
              <td>{renderBoolean(form.bodyOfVehicle)}</td>
              <td>{renderBoolean(form.tiresConditionAndAirPressure)}</td>
              <td>{renderBoolean(form.horn)}</td>
              <td>{renderBoolean(form.stateInspectionAndLicSticker)}</td>
              <td>{renderBoolean(form.wipersReservoir)}</td>
              <td>{renderBoolean(form.lowAndHighBeamHeadlights)}</td>
              <td>{renderBoolean(form.brakeLights)}</td>
              <td>{renderBoolean(form.turnSignalLights)}</td>
              <td>{renderBoolean(form.emergencyFlasherLights)}</td>
              <td>{renderBoolean(form.vehicleInsuranceCardValid)}</td>
              <td>{renderBoolean(form.gasTankFull)}</td>
              <td>{renderBoolean(form.emergencyInstructions)}</td>
              <td>{renderBoolean(form.washVehicle)}</td>
              <td>{form.comments}</td>
              <td>{form.dpwSignature}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <button onClick={handleRemove} className="button">
        Remove Selected
      </button>
    </div>
  );
}

export default MaintenanceReportTable;
