import React, { useState, useEffect } from "react";
import axios from "axios";

function Equipment() {
  const [equipmentData, setEquipmentData] = useState([]);
  let [selectedVehicles, setSelectedEquipment] = useState([]);

  useEffect(() => {
    async function fetchEquipmentData() {
      try {
        const response = await axios.get("http://localhost:5000/equipment/", {
          withCredentials: true,
        });
        setEquipmentData(response.data);
      } catch (error) {
        console.error("Fetch error:", error);
      }
    }

    fetchEquipmentData();
  }, []);

  return (
    <div>
      <h2>Equipment List</h2>
      <table>
        <thead>
          <tr>
            <th>Select</th>
            <th>Equipment ID</th>
            <th>Name</th>
            <th>Type</th>
            <th>Entry Date</th>
          </tr>
        </thead>
        <tbody>
          {equipmentData.map((equipment) => (
            <tr key={equipment.id}>
              <td>
                <input
                  type="checkbox"
                  checked={selectedVehicles.includes(vehicle.vehicleID)}
                  onChange={() => handleCheckboxChange(vehicle.vehicleID)}
                />
              </td>
              <td>{equipment.equipmentID}</td>
              <td>{equipment.equipmentDescription}</td>
              <td>{equipment.equipmentCondition}</td>
              <td>{equipment.createdAt}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <button onClick={handleUpdate} className="button">
        Update Selected
      </button>
      <button onClick={handleRemove} className="button">
        Remove Selected
      </button>
    </div>
  );
}

export default Equipment;
