import React, { useState, Suspense } from "react";
import Sidebar from "../components/Sidebar.jsx";
import MainContent from "../components/MainContent.jsx";

const ClassBCDLForm = React.lazy(() =>
  import("../components/Forms/classBCDL.jsx")
);
const DamageReport = React.lazy(() =>
  import("../components/Forms/DamageReport.jsx")
);
const Equipment = React.lazy(() => import("../components/Forms/Equipment.jsx"));
const localInspection = React.lazy(() =>
  import("../components/Forms/localInspection.jsx")
);
const Maintenance = React.lazy(() =>
  import("../components/Forms/Maintenance.jsx")
);
const Vehicle = React.lazy(() => import("../components/Forms/Vehicle.jsx"));

const Dashboard = () => {
  const [activeMenu, setActiveMenu] = useState(""); // State to track the active menu
  const [activeComponent, setActiveComponent] = useState(null); // State to manage active component

  const handleMenuClick = (menu) => {
    // Function to update the activeMenu state
    setActiveMenu(menu);

    // Determine which component to display based on the active menu item
    switch (menu) {
      case "ClassBCDL":
        setActiveComponent(<ClassBCDLForm />);
        break;
      // Add more cases for other menu items if needed
      default:
        setActiveComponent(null);
    }
  };

  return (
    <div className="dashboard">
      <Sidebar onMenuClick={handleMenuClick} />
      <MainContent activeMenu={activeMenu}>
        {activeComponent} {/* Render the active component */}
      </MainContent>
    </div>
  );
};

export default Dashboard;
