import React, { useState } from "react";
import axios from "axios";

function DamageReport() {
  const [formData, setFormData] = useState({
    description: "",
    userID: "",
    vehicleID: "",
    equipmentID: "",
    reportDate: "",
    negligent: false,
    pictures: null,
  });

  const handleChange = (e) => {
    const { name, value, type, checked, files } = e.target;
    setFormData({
      ...formData,
      [name]: type === "checkbox" ? checked : type === "file" ? files : value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Perform your Axios POST request here with formData
      const response = await axios.post(
        "http://localhost:5000/damage-reports",
        formData,
        { withCredentials: true }
      );

      if (response.status === 200) {
        console.log("Damage report submitted successfully");
        // Handle success logic here
      } else {
        console.error("Damage report submission failed");
        // Handle errors here
      }
    } catch (error) {
      console.error(
        "An error occurred while submitting the damage report",
        error
      );
      // Handle errors here
    }
  };

  return (
    <div>
      <h2>Create Damage Report</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="description">Description:</label>
          <textarea
            id="description"
            name="description"
            value={formData.description}
            onChange={handleChange}
          />
        </div>

        <div>
          <label htmlFor="userID">User ID:</label>
          <input
            type="text"
            id="userID"
            name="userID"
            value={formData.userID}
            onChange={handleChange}
          />
        </div>

        <div>
          <label htmlFor="vehicleID">Vehicle ID:</label>
          <input
            type="text"
            id="vehicleID"
            name="vehicleID"
            value={formData.vehicleID}
            onChange={handleChange}
          />
        </div>

        <div>
          <label htmlFor="equipmentID">Equipment ID:</label>
          <input
            type="text"
            id="equipmentID"
            name="equipmentID"
            value={formData.equipmentID}
            onChange={handleChange}
          />
        </div>

        <div>
          <label htmlFor="reportDate">Report Date:</label>
          <input
            type="date"
            id="reportDate"
            name="reportDate"
            value={formData.reportDate}
            onChange={handleChange}
          />
        </div>

        <div>
          <label htmlFor="negligent">Negligent:</label>
          <input
            type="checkbox"
            id="negligent"
            name="negligent"
            checked={formData.negligent}
            onChange={handleChange}
          />
        </div>

        <div>
          <label htmlFor="pictures">Pictures:</label>
          <input
            type="file"
            id="pictures"
            name="pictures"
            multiple
            onChange={handleChange}
          />
        </div>

        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default DamageReport;
